import * as React from 'react';
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import { Button, CardActionArea, CardActions } from '@mui/material';
import { BrowserRouter as Router, Switch, 
  Route, Redirect,} from "react-router-dom";
  import { createTheme, ThemeProvider } from '@material-ui/core/styles';
import { color, fontStyle } from '@mui/system';


export default function CourseCard() {
  



  
  const routeChange = () =>{ 
    this.context.location.transitionTo('HomePage');
  
  }


  const theme = createTheme({
    palette: {
      background:"#5B84B1FF",
      primary: {
      main: "#5B84B1FF"
      },
      secondary: {
      main: "#FC766AFF"
      }
    },
    typography: {
      fontFamily: [
        "Josefin Sans"
      ],
      h5: {
        color:"white"
      },
    }
    });


  return (
    <Card sx={{ maxWidth: 445,backgroundColor:"#FC766AFF" }}>
      <CardActionArea>
        <CardMedia
          component="img"
          height="140"
          image="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAASwAAACoCAMAAABt9SM9AAAAA1BMVEVbhLEkGiLeAAAAR0lEQVR4nO3BAQEAAACCIP+vbkhAAQAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAO8GxYgAAb0jQ/cAAAAASUVORK5CYII="
          alt="green iguana"
        />
        <CardContent>
          <Typography gutterBottom variant="h5"   sx={{ color:"white"
  }} component="div">
            Software Construction
          </Typography>
          <Typography variant="body2" sx={{ color:"white"
  }}>
            Credits 3+1
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small"  sx={{
    color: 'white',
    border:"2px solid",
  }} href={"/ClMapping"}>
          Grading
        </Button>
        <Button size="small" sx={{
    color: 'white',
    border:"2px solid",
  }} href={"/ClMapping"}>
          CLO Mapping
        </Button>
      </CardActions>
    </Card>
  );
}
